import React, { Component, useState } from 'react';
import {
  Card, CardText, CardBody,
  CardTitle, CardSubtitle, 
  Jumbotron, Button,
  TabContent, TabPane, Nav, NavItem, NavLink, Row, Col
} from 'reactstrap';
import classnames from 'classnames';
import { GiFalling } from "react-icons/gi";
import Icon from 'react-fa';
import rp from "request-promise";
import cheerio from "cheerio";
import Carousel from '@brainhubeu/react-carousel';
import '@brainhubeu/react-carousel/lib/style.css';

class ComingSoon extends Component {

      constructor() {
        super()
        this.state = { 
            value: 0,
            dates: [],
            activeTab: '1'
        };
        this.onChange = this.onChange.bind(this);
      }

      async componentDidMount() {
        await rp("https://cors-anywhere.herokuapp.com/https://www.imdb.com/calendar?region=US&ref_=rlm")
        .then(html => {
          let dates= [];
          let $ = cheerio.load(html);

          $("#main h4").each(function() {
            let date = $(this)
              .prepend()
              .text();

            dates.push(date);
          });
          this.setState({
            dates: dates
          })


        })
        .catch(function(err) {
          console.log(err);
        });
      }

      onChange(value) {
        this.setState({ value })
      }

      render() {

        const toggle = tab => {
            if(this.state.activeTab !== tab) this.setState({activeTab: tab});
        }

        return (
        <div>
            <h2 className="section-title">Coming Sooooooon</h2>

            <div className="tabs">
                <Nav tabs>
                    {this.state.dates.map(item => {
                        const tabName = item.split(" ")[1]+item.split(" ")[2];
                        return(<NavItem key={item} className="nav-items">
                            <NavLink
                                className={classnames({ active: this.state.activeTab === {tabName} })}
                                onClick={() => { toggle({tabName}); }}
                            >
                                {tabName}
                            </NavLink>
                        </NavItem>)
                    })}
                </Nav>
                <TabContent activeTab={this.state.activeTab}>
                    <TabPane tabId="1111">
                        <Row>
                            <Col sm="12">
                            <h4>Tab 1 Contents</h4>
                            </Col>
                        </Row>
                    </TabPane>
                    <TabPane tabId="2">
                        <Row>
                            <Col sm="6">
                                <Card body>
                                    <CardTitle>Special Title Treatment</CardTitle>
                                    <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                                    <Button>Go somewhere</Button>
                                </Card>
                            </Col>
                            <Col sm="6">
                            <Card body>
                                <CardTitle>Special Title Treatment</CardTitle>
                                <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                                <Button>Go somewhere</Button>
                            </Card>
                            </Col>
                        </Row>
                    </TabPane>
                </TabContent>
            </div>
        </div>
        );
      }
  }



  export default ComingSoon;
