import React, {useState} from 'react';
import {
    Carousel,
    CarouselItem,
    CarouselControl,
    CarouselIndicators,
    CarouselCaption
  } from 'reactstrap';

const items = [
{
    src: 'https://m.media-amazon.com/images/M/MV5BMTFiNDlmOTMtZGM5MC00OWQzLWE4YmYtZDBlY2NiZDMyNGNlXkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_SY1000_CR0,0,677,1000_AL_.jpg',
    altText: 'Slide 1',
    caption: 'Invisible Life'
},
{
    src: 'https://m.media-amazon.com/images/M/MV5BZGQ0OGJiZWEtZWE4NS00NWU3LThiM2QtYzdmMjhhZGM1MjBhXkEyXkFqcGdeQXVyODc0OTEyNDU@._V1_SY1000_SX675_AL_.jpg',
    altText: 'Slide 2',
    caption: '1917'
},
{
    src: 'https://m.media-amazon.com/images/M/MV5BZjNmYzJiOTAtYzBmZS00NDI0LTkzOTEtYWY4OWI2MTg2MmViXkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_SY1000_CR0,0,670,1000_AL_.jpg',
    altText: 'Slide 3',
    caption: 'Jojo Rabbit'
}
];

const Carousels = (props) => {
    const [activeIndex, setActiveIndex] = useState(0);
    const [animating, setAnimating] = useState(false);

    const next = () => {
      if (animating) return;
      const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
      setActiveIndex(nextIndex);
    }

    const previous = () => {
      if (animating) return;
      const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
      setActiveIndex(nextIndex);
    }

    const goToIndex = (newIndex) => {
      if (animating) return;
      setActiveIndex(newIndex);
    }

    const slides = items.map((item) => {
      return (
        <CarouselItem
          onExiting={() => setAnimating(true)}
          onExited={() => setAnimating(false)}
          key={item.src}
          className="carousel-item" 
        >
          <img src={item.src} alt={item.altText} />
          <CarouselCaption captionHeader={item.caption} />
        </CarouselItem>
      );
    });

    return (
      <Carousel
        activeIndex={activeIndex}
        next={next}
        previous={previous}
        className="carousel-inner"
      >
        <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={goToIndex} />
        {slides}
        <CarouselControl direction="prev" directionText="Previous" onClickHandler={previous} />
        <CarouselControl direction="next" directionText="Next" onClickHandler={next} />
      </Carousel>
    );
  }

export default Carousels;
