import React, { Component, useState } from 'react';
import {
  Card, CardText, CardBody,
  CardTitle, CardSubtitle, 
  Jumbotron, Button,
  TabContent, TabPane, Nav, NavItem, NavLink, Row, Col
} from 'reactstrap';
import classnames from 'classnames';
import { GiFalling } from "react-icons/gi";
import Icon from 'react-fa';
import rp from "request-promise";
import cheerio from "cheerio";
import Carousel from '@brainhubeu/react-carousel';
import '@brainhubeu/react-carousel/lib/style.css';

const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
}

class ComingSoon extends Component {

      constructor() {
        super()
        this.state = { 
            value: 0,
            apis: [],
            activeTab: '1'
        };
        this.onChange = this.onChange.bind(this);
      }

      async componentDidMount() {
        await rp("https://cors-anywhere.herokuapp.com/https://www.imdb.com/calendar?region=US&ref_=rlm")
        .then(html => {
          let apis= [];
          let $ = cheerio.load(html);

          $("#main ul li a").each(function() {
            let name = $(this)
              .prepend()
              .text();

              fetch("http://www.omdbapi.com/?apikey=1e54e73e&t="+name)
              .then(response => {
                  response.json().then (data =>{
                      apis.push(data);
                      console.log(apis)
                  })
              })
              .catch(err => {
                  console.log(err);
              });

          });
          this.setState({
            apis: apis,
            laoding: true
          })


        })
        .catch(function(err) {
          console.log(err);
        });
      }

      onChange(value) {
        this.setState({ value })
      }

      render() {

        const [activeTab, setActiveTab] = useState('1');



        return (
        <div className="">
            <h2 className="section-title">Coming Sooooooon</h2>

            <Nav tabs>
                <NavItem>
                <NavLink
                    className={classnames({ active: activeTab === '1' })}
                    onClick={() => { toggle('1'); }}
                >
                    Tab1
                </NavLink>
                </NavItem>
                <NavItem>
                <NavLink
                    className={classnames({ active: activeTab === '2' })}
                    onClick={() => { toggle('2'); }}
                >
                    Moar Tabs
                </NavLink>
                </NavItem>
            </Nav>
            <TabContent activeTab={activeTab}>
                <TabPane tabId="1">
                <Row>
                    <Col sm="12">
                    <h4>Tab 1 Contents</h4>
                    </Col>
                </Row>
                </TabPane>
                <TabPane tabId="2">
                <Row>
                    <Col sm="6">
                    <Card body>
                        <CardTitle>Special Title Treatment</CardTitle>
                        <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                        <Button>Go somewhere</Button>
                    </Card>
                    </Col>
                    <Col sm="6">
                    <Card body>
                        <CardTitle>Special Title Treatment</CardTitle>
                        <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                        <Button>Go somewhere</Button>
                    </Card>
                    </Col>
                </Row>
                </TabPane>
            </TabContent>

        </div>
        );
      }
  }



  export default ComingSoon;
