import React, {Component} from 'react';
import {
  Row,
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button,
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';
import { FaImdb, FaCreativeCommonsSamplingPlus } from "react-icons/fa";
import Icon from 'react-fa';
import rp from "request-promise";
import cheerio from "cheerio";
import Carousels, { Dots } from '@brainhubeu/react-carousel';

class InTheater extends Component {

    constructor() {
        super()
        this.state = { 
            value: 0,
            apis: []
        };
        this.onChange = this.onChange.bind(this);
      }

      componentDidMount() {
        // use the request-promise library to fetch the HTML from pokemon.org
        rp("https://cors-anywhere.herokuapp.com/https://www.imdb.com/search/title/?title_type=feature&user_rating=7.0,&groups=now-playing-us")
          .then(html => {
            let apis= [];
            let $ = cheerio.load(html);

            // find what element ids, classes, or tags you want from opening console in the browser
            // cheerio library lets you select elements similar to querySelector
            $(".lister-item-header a").each(function(i, element) {
              let name = $(this)
                .prepend()
                .text();

                fetch("https://imdb-internet-movie-database-unofficial.p.rapidapi.com/film/"+name, {
                    "method": "GET",
                    "headers": {
                        "x-rapidapi-host": "imdb-internet-movie-database-unofficial.p.rapidapi.com",
                        "x-rapidapi-key": "c3bfcbae69msh2fefac5f7da67adp1d2687jsnade70055f49b"
                    }
                })
                .then(response => {
                    response.json().then (data =>{
                        apis.push(data);
                    })

                })
                .catch(err => {
                    console.log(err);
                });
            });

            this.setState({ apis });
          })
          .catch(function(err) {
            console.log(err);
          });
      }

      onChange(value) {
        this.setState({ value });
      }

      render() {
        return (
        <div className="carousel-inners">
            <h2 className="section-title"><a href="/">In Theater</a></h2>
            <Carousels
                value={this.state.value}
                onChange={this.onChange}
                arrowLeft={<Icon name="angle-double-left" />}
                arrowLeftDisabled={<Icon name="angle-left" />}
                arrowRight={<Icon name="angle-double-right" />}
                arrowRightDisabled={<Icon name="angle-right" />}
                addArrowClickHandler
                clickToChange
                slidesPerPage={3}
                infinite
                className="carousel-item"
            >

                    {this.state.apis.map(item => {
                        return(<Card key={item.id} className="card">
                            <img src='https://m.media-amazon.com/images/M/MV5BMTFiNDlmOTMtZGM5MC00OWQzLWE4YmYtZDBlY2NiZDMyNGNlXkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_SY1000_CR0,0,677,1000_AL_.jpg' />
                            <CardBody>
                                <CardTitle className="card-title">{item}</CardTitle>
                                <CardSubtitle><FaImdb /></CardSubtitle>
                                <CardSubtitle>Director: </CardSubtitle>
                                {/*<CardText>{item.intro}</CardText>
                                <Button>Button</Button>*/}
                            </CardBody>
                        </Card>);
                    })}


            </Carousels>
        </div>
        );
      }
  }



  export default InTheater;
