import React, {Component} from 'react';
import {
  Row,
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button,
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';
import { FaImdb } from "react-icons/fa";
import rp from "request-promise";
import cheerio from "cheerio";

class InTheater extends Component {

    constructor(props) {
        super(props);
        this.state = {
            info: [],
            activeIndex: 0,
            animating: false
        };
    }

    componentDidMount() {

      rp("https://www.imdb.com/search/title/?title_type=feature&user_rating=7.0,&groups=now-playing-us")
        .then(html => {
          let names = [];
          let $ = cheerio.load(html);

          // find what element ids, classes, or tags you want from opening console in the browser
          // cheerio library lets you select elements similar to querySelector
          $(".lister-item-header a").each(function(i, element) {
            let name = $(this)
              .prepend()
              .text();
            names.push(name);
          });

          this.setState({ names });
        })
        .catch(function(err) {
          console.log("crawl failed");
        });
    }

    next() {
        if (this.animating) return;
        const nextIndex = this.activeIndex === this.info.length - 1 ? 0 : this.activeIndex + 1;
        this.setState.activeIndex=nextIndex;
    }

    previous() {
        if (this.animating) return;
        const nextIndex = this.activeIndex === 0 ? this.info.length - 1 : this.activeIndex - 1;
        this.setState.activeIndex=nextIndex;
    }

    goToIndex(newIndex) {
        if (this.animating) return;
        this.setState.activeIndex=newIndex;
    }

    slides(items){
        items.map((item) => {
        return (
          <CarouselItem
            onExiting={() => this.setState.animating(true)}
            onExited={() => this.setState.animating(false)}
            key={item}
            className="carousel-item"
          >
            <h2 className="section-title"><a href="/">In Theater</a></h2>
            {/*<a href={item.trailer} title="traaailer" className="card-img" rel="noopener noreferrer" target="_blank"><img src={item.src} alt={item.title} /></a>*/}
            <Card>
              <CardBody>
                <CardTitle className="card-title">{item}</CardTitle>
                {/*<CardSubtitle><FaImdb />{item.imdbscore}</CardSubtitle>
                <CardSubtitle>Director: {item.director}</CardSubtitle>
                <CardText>{item.intro}</CardText>
                <Button>Button</Button>*/}
              </CardBody>
            </Card>
          </CarouselItem>

        );
      });
    }


    render() {
      return (
        <Carousel
            activeIndex={this.activeIndex}
            next={this.next}
            previous={this.previous}
            className="carousel-inners"
            interval="1000000"
        >
            <CarouselIndicators items={this.items} activeIndex={this.activeIndex} onClickHandler={this.goToIndex} />
            {this.slides}
            <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
            <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} />
        </Carousel>
      );
    }
  }



  export default InTheater;
