import React, {Component} from 'react';
import {
  Row,
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button,
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';
import { FaImdb } from "react-icons/fa";
import rp from "request-promise";
import cheerio from "cheerio";
import Carousels, { Dots } from '@brainhubeu/react-carousel';

class InTheater extends Component {

    constructor() {
        super()
        this.state = { value: 0 };
        this.onChange = this.onChange.bind(this);
      }

      onChange(value) {
        this.setState({ value });
      }

      render() {
        return (
        <div>
          <input
            type="number"
            value={this.state.value}
            onChange={e => this.onChange(parseInt(e.target.value || 0))}
          />
          <Carousels
            value={this.state.value}
            onChange={this.onChange}

            slides={[
              (<img key={this.state.value} src='https://m.media-amazon.com/images/M/MV5BMTFiNDlmOTMtZGM5MC00OWQzLWE4YmYtZDBlY2NiZDMyNGNlXkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_SY1000_CR0,0,677,1000_AL_.jpg' />)
            ]}
            arrows
            clickToChange
          />
        </div>
        );
      }
  }



  export default InTheater;
