import { GiFalling } from "react-icons/gi";
import Icon from 'react-fa';
import rp from "request-promise";
import cheerio from "cheerio";
import Carousel from '@brainhubeu/react-carousel';
import '@brainhubeu/react-carousel/lib/style.css';

class ComingSoon extends Component {

      constructor() {
        super()
        this.state = { 
            value: 0,
            apis: []
        };
        this.onChange = this.onChange.bind(this);
      }

      async componentDidMount() {
        await rp("https://cors-anywhere.herokuapp.com/https://www.imdb.com/calendar?region=US&ref_=rlm")
        .then(html => {
          let apis= [];
          let $ = cheerio.load(html);

          $("ul li a").each(function() {
            let name = $(this)
              .prepend()
              .text();

              fetch("http://www.omdbapi.com/?apikey=1e54e73e&"+name)
              .then(response => {
                  response.json().then (data =>{
                      apis.push(data);
                      console.log(apis)
                  })
              })
              .catch(err => {
                  console.log(err);
              });

          });
          this.setState({
            apis: apis,
            laoding: true
          })


        })
        .catch(function(err) {
          console.log(err);
        });
      }

      onChange(value) {
        this.setState({ value })
      }

      render() {

        return (
        <div className="carousel-inners">
            <h2 className="section-title">Coming Sooooooon</h2>

            {/*<Carousel
