// body component for loading stuff about an individual movie
// when the user clicks on it

import React from "react";
import "../index.css";
import { Media, Container, Row, Col } from 'reactstrap';
import { NavLink } from "react-router-dom";

class Movie extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      movie: {},
      movies: [],
      loading: true
    };
  }

  componentDidMount(){
    fetch("https://imdb-internet-movie-database-unofficial.p.rapidapi.com/film/"+this.props.location.id, {
            "method": "GET",
            "headers": {
                "x-rapidapi-host": "imdb-internet-movie-database-unofficial.p.rapidapi.com",
                "x-rapidapi-key": "c3bfcbae69msh2fefac5f7da67adp1d2687jsnade70055f49b"
            }
    })
    .then(response => {
      response.json().then (data =>{
          this.setState({
            movie: data
          }) 
      })
    })
    .catch(err => {
        console.log(err);
    }); 

    fetch('https://www.omdbapi.com/?apikey=1e54e73e&i='+this.props.location.id)
    .then(response => {
      response.json().then (data =>{
          /* this.setState({
            movies: data.Search
          }) */
          console.log(data)
      })
    })
    .catch(err => {
        console.log(err);
    }); 
  }

  // below I added a link back to the landing page, just to make our lives easier while testing
  // we'll remove it when we actually build the events page, since users won't need to go back to
  // the landing page
  render() {
    return (
      <Container>
        {this.state.movie? 
        <div>
          <Row>
            <Col>
            <a className="movie-title" rel="noopener noreferrer" target="_blank" href={"https://www.imdb.com/title/"+this.state.movie.id}>{this.state.movie.title} ({this.state.movie.year})</a></Col></Row>
        <Media>
        <Media left>
          <img alt={this.state.movie.title} src={this.state.movie.poster}/>
        </Media>
        <Media body>
          <Media heading>
            Media heading
          </Media>
          Cras sit amet nibh libero, in gravida nulla. Nulla vel metus scelerisque ante sollicitudin commodo. Cras purus odio, vestibulum in vulputate at, tempus viverra turpis. Fusce condimentum nunc ac nisi vulputate fringilla. Donec lacinia congue felis in faucibus.
        </Media></Media></div>: <Media>wait a sec bru</Media>}
      </Container>
    );
  }
}

export default Movie;
