import React, { useState } from "react";
import "../App.css";
import { NavLink } from "react-router-dom";
import {
  Button,
  Input,
  Modal,
  ModalBody,
  ModalFooter
} from "reactstrap";
import { FiSearch, FiXCircle } from "react-icons/fi";

const Search = (props) => {
  const [searchValue, setSearchValue] = useState("");
  const [toggleSearch, setToggleSearch] = useState(false);

  const handleSearchInputChanges = (e) => {
    setSearchValue(e.target.value);
  }

  const handleToggleSearch = () => {
    setToggleSearch(!toggleSearch)
  }

  const resetInputField = () => {
    setSearchValue("")
  }

  const callSearchFunction = (e) => {
    e.preventDefault();
    props.search(searchValue);
    resetInputField();
  }

  return (
      <Modal isOpen={toggleSearch} toggle={handleToggleSearch}>
        <ModalBody>
          <Input
            value={searchValue}
            onChange={handleSearchInputChanges}
            type="text"
            placeholder="name"
          />
          <Button className="btn-search" color="link" onClick={callSearchFunction} type="submit" value="SEARCH">search me <FiSearch /></Button>
          <Button className="btn-search" color="link" onClick={handleToggleSearch}>maybe later <FiXCircle /></Button>
        </ModalBody>
      </Modal>
    );
}

export default Search;
